package week1.day1;

public class IfElse_Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=-1;
				
		if (num<0)
			System.out.println("Negative");
		else if(num==0)
			System.out.println("Zero");
		else
			System.out.println("Positive");
	}

}
